query FindDashboard($session_filter: Int_comparison_exp!, $user: String!, $slug: String!) {
  dashboards(
    where: {slug: {_eq: $slug}, _or: [{user: {name: {_eq: $user}}}, {team: {handle: {_eq: $user}}}]}
  ) {
    ...Dashboard
    favorite_dashboards(where: {user_id: $session_filter}, limit: 1) {
      created_at
      __typename
    }
    __typename
  }
}

fragment Dashboard on dashboards {
  id
  name
  slug
  is_private
  is_archived
  created_at
  updated_at
  tags
  user {
    ...User
    __typename
  }
  team {
    ...Team
    __typename
  }
  forked_dashboard {
    slug
    name
    user {
      name
      __typename
    }
    team {
      handle
      __typename
    }
    __typename
  }
  text_widgets {
    id
    created_at
    updated_at
    text
    options
    __typename
  }
  visualization_widgets {
    id
    created_at
    updated_at
    options
    visualization {
      ...Visualization
      __typename
    }
    __typename
  }
  param_widgets {
    id
    key
    visualization_widget_id
    query_id
    dashboard_id
    options
    created_at
    updated_at
    __typename
  }
  dashboard_favorite_count_all {
    favorite_count
    __typename
  }
  trending_scores {
    score_1h
    score_4h
    score_24h
    updated_at
    __typename
  }
  __typename
}

fragment User on users {
  id
  name
  profile_image_url
  __typename
}

fragment Team on teams {
  id
  name
  handle
  profile_image_url
  __typename
}

fragment Visualization on visualizations {
  id
  type
  name
  options
  created_at
  query_details {
    query_id
    name
    description
    show_watermark
    parameters
    user {
      id
      name
      profile_image_url
      __typename
    }
    team {
      id
      name
      handle
      profile_image_url
      __typename
    }
    __typename
  }
  __typename
}
